import React from 'react';
import {ethers} from 'ethers';
import axios from 'axios';

const API_URL = 'http://localhost:4000';

const ITEMS = [{
    id: 1,
    price: ethers.utils.parseEther('3')
},{
    id: 2,
    price: ethers.utils.parseEther('4')
}
];

function Store({paymentProcessor, usdt}) {
    const buy = async item => {
        const response1 = await axios.get(`${API_URL}/api/getPaymentId/${item.id}`);
        const tx1 = await usdt.approve(paymentProcessor.address, amount);
        await tx1.wait(); //waiting for transaction to finalize

        const tx2 = await paymentProcessor.pay(item.price, response1.data.paymentId);
        await tx2.wait();

        await new Promise(resolve => setTimeout(resolve, 5000)); //waiting for backend to update payment record

        const response2 = await axios.get(`${API_URL}/api/getItemUrl/${response1.data.paymentId}`);
        console.log(response2);
    };

    return (
        <ul className='list-group'>
            <li className='list-group-item'>
                Buy Item1 - <span className='front-weight-bold'>3</span>
                <button type='button'
                className='btn btn-primary float-right'
                onClick={()=>this.buy(ITEMS[0])}>
                    Buy
                </button>
            </li>
            <li className='list-group-item'>
                Buy Item2 - <span className='front-weight-bold'>4</span>
                <button type='button'
                className='btn btn-primary float-right'
                onClick={()=>this.buy(ITEMS[1])}>
                    Buy
                </button>
            </li>
        </ul>
    )
}

export default Store;
