import React, {useState, useEffect} from 'react';
import getBlockchain from './ethereum.js';
import Store from './Store.js';

function App() {
  const [paymentProcessor, setPaymentProcessor] = useState(undefined);
  const [usdt, setUsdt] = useState(undefined);

  useEffect(() => {
    const init = async () => {
      const {getPaymentProcessor, usdt} = await getBlockchain();
      setPaymentProcessor(paymentProcessor);
      setUsdt(usdt);
    }
    init();
  }, [])

  if(typeof window.ethereum === 'undefined') {
    return (
      <div className='container'>
        <div className='col-sm-12'>
          <h1>Blockchain Ecommerce App</h1>
          <p>You need to install the MetaMask</p>
        </div>
      </div>
    )
  }
  return (
    <div className="container">
      <div className='col-sm-12'>
        <h1>Blockchain Ecommerce App</h1>
        <Store paymentProcessor={paymentProcessor} usdt={usdt}/>
      </div>
    </div>
  );
}

export default App;
