import React from 'react';
import {ethers} from 'ethers';
import axios from 'axios';

const API_URL = 'http://localhost:4000';

const ITEMS = [{
    id: 1,
    price: ethers.utils.parseEther('1')
},{
    id: 2,
    price: ethers.utils.parseEther('2')
}
];

function Store({ paymentProcessor, usdt }) {
    const buy = async item => {
      const response1 = await axios.get(`${API_URL}/api/getPaymentId/${item.id}`);
      console.log(response1);

      console.log('processor address: '+ paymentProcessor.address);
      console.log('item price: '+ item.price);
      console.log(usdt);

      let instance = await usdt.deployed();
      console.log(instance);
      const  tx1 = await instance.approve(paymentProcessor.address, item.price);
      await tx1.wait();
      console.log(tx1.value.toString());
      //const tx1 = await usdt.approve(paymentProcessor.address, item.price);  
    //   await tx1.wait();
    //   console.log('tx1: '+tx1);

        console.log('dnm');
      const tx2 = await paymentProcessor.pay(item.price, response1.data.paymentId);
      console.log('here');
      const receipt = await tx2.wait();
      console.log('tx2: '+tx2);

      await new Promise(resolve => setTimeout(resolve, 5000)); 
      const response2 = await axios.get(`${API_URL}/api/getItemUrl/${response1.data.paymentId}`);
      console.log(response2);
    };

    return (
        <ul className='list-group'>
            <li className='list-group-item'>
                Buy Item1 - <span className='front-weight-bold'>3</span>
                <button 
                type='button'
                className='btn btn-primary float-right'
                onClick={()=>buy(ITEMS[0])}>
                    Buy
                </button>
            </li>
            <li className='list-group-item'>
                Buy Item2 - <span className='front-weight-bold'>4</span>
                <button type='button'
                className='btn btn-primary float-right'
                onClick={()=>buy(ITEMS[1])}>
                    Buy
                </button>
            </li>
        </ul>
    )
}

export default Store;
