import React, { useState, useEffect } from 'react';
import {ethers} from 'ethers';
import axios from 'axios';

const API_URL = 'http://localhost:4000';

const ITEMS = [{
    id: 1,
    price: ethers.utils.parseEther('1')
},{
    id: 2,
    price: ethers.utils.parseEther('2')
}
];

function Store({ paymentProcessor, usdt }) {
    const [isLoading, setIsLoading] = useState(false);
    const [errorMessage, setErrorMessage] = useState("");
    const buy = async item => {
      setIsLoading(true);
      const response1 = await axios.get(`${API_URL}/api/getPaymentId/${item.id}`);

      let instance = await usdt.deployed();
      const  tx1 = await instance.approve(paymentProcessor.address, item.price);
      await tx1.wait();
      //const tx1 = await usdt.approve(paymentProcessor.address, item.price);  

      //console.log('itemPrice: '+item.price + ' paymentId:' + response1.data.paymentId);
      const tx2 = await paymentProcessor.pay(item.price, response1.data);
      const receipt = await tx2.wait();

      await new Promise(resolve => setTimeout(resolve, 5000)); 
      const response2 = await axios.get(`${API_URL}/api/getItemUrl/${response1.data}`)
      .then((response) => {
          setIsLoading(false);
      }).catch(()=>{
          setErrorMessage('Unable to get response');
          setIsLoading(false);
      });
      console.log(response2);
    };

    return (
        <ul className='list-group'>
            <li className='list-group-item'>
                Buy Item1 - <span className='front-weight-bold'>3</span>
                <button 
                type='button'
                className='btn btn-primary float-right'
                onClick={()=>buy(ITEMS[0])} disabled={isLoading}>
                    Buy
                </button>
            </li>
            <li className='list-group-item'>
                Buy Item2 - <span className='front-weight-bold'>4</span>
                <button type='button'
                className='btn btn-primary float-right'
                onClick={()=>buy(ITEMS[1])}>
                    Buy
                </button>
            </li>
        </ul>
    )
}

export default Store;
