import React from 'react';
import {ethers} from 'ethers';
import axios from 'axios';

const API_URL = 'http://localhost:4000';
const SellerPubKey = '003X9PaFsuG2XUxV6OPHYiTR6buqNPCbeZzOxtCiuWE='; //0xf73...

const ITEMS = [{
    id: 1,
    price: ethers.utils.parseEther('1')
},{
    id: 2,
    price: ethers.utils.parseEther('2')
}
];

function Store({ paymentProcessor, usdt, publicKey }) {
    const buy = async item => {
    //console.log(publicKey);
      //const response1 = await axios.get(`${API_URL}/api/getPaymentId/${item.id}/${publicKey}`);

      const requestObject = JSON.stringify({
                    itemId: item.id,
                    pubKey: item.publicKey,
                    name: 'Fred Flinstone',
                    mail: 'fflinstone@yeeha.com',
                    address: 'stonehange st no 34',
                    phone: '+9012345678'
                });
      const response1 = await axios.post(`${API_URL}/api/getPaymentId`, requestObject);
      console.log(response1);
      let instance = await usdt.deployed();
      const  tx1 = await instance.approve(paymentProcessor.address, item.price);
      await tx1.wait();
      //const tx1 = await usdt.approve(paymentProcessor.address, item.price);  
      //const response3 = await axios.get(`${API_URL}/api/encryptWithPK/'satoshi nakomoto'/${publicKey}`);
      //console.log('itemPrice: '+item.price + ' paymentId:' + response1.data.paymentId);

      const encrypted = await axios.post(`${API_URL}/api/encryptWithPK/`, {
        message: response1.data,
        pubKey: SellerPubKey
        });
      console.log(encrypted);

      const tx2 = await paymentProcessor.pay(item.price, JSON.stringify(encrypted));
      const receipt = await tx2.wait();

      await new Promise(resolve => setTimeout(resolve, 5000)); 
      const response2 = await axios.get(`${API_URL}/api/getItemUrl/${response1.data}`)
      console.log(response2);
    };

    return (
        <ul className='list-group'>
            <li className='list-group-item'>
                Buy Item1 - <span className='front-weight-bold'>3</span>
                <button 
                type='button'
                className='btn btn-primary float-right'
                onClick={()=>buy(ITEMS[0])}>
                    Pay
                </button>
            </li>
            <li className='list-group-item'>
                Buy Item2 - <span className='front-weight-bold'>4</span>
                <button type='button'
                className='btn btn-primary float-right'
                onClick={()=>buy(ITEMS[1])}>
                    Pay
                </button>
            </li>
        </ul>
    )
}

export default Store;
